mapper.select:
select

from GOODS_LIST
where 1=1

  and mall_name = #{mallName,jdbcType=VARCHAR}


  and category = #{category,jdbcType=VARCHAR}


  and title like CONCAT('%',#{keyword,jdbcType=VARCHAR},'%')

and publish = 1
order by sync_time desc
limit #{startIndex,jdbcType=BIGINT},#{pageSize,jdbcType=BIGINT}

select count(id) from GOODS_LIST where 1=1
and mall_name = #{mallName,jdbcType=VARCHAR}
and category = #{category,jdbcType=VARCHAR}
and title like CONCAT('%',#{keyword,jdbcType=VARCHAR},'%')
and publish = 1
